INSERT INTO `question` VALUES (5, '5. Норматив-ҳуқуқий ҳужжатларда коллизияларни бартараф этиш масалаларини таҳлил этинг.', 11, 'Давлат ва ҳуқуқ назарияси', 1, 0);
