INSERT INTO `question` VALUES (237, '62.Тадбиркорлик фаолияти субекти ҳуқуқларининг устуворлиги принципи моҳиятини тушунтиринг.', 13, 'Тадбиркорлик ҳуқуқи', 2, 1);
INSERT INTO `question` VALUES (238, '63. Қонунчиликдаги сўнги ўзгаришларга кўра тадбиркорлик субектлари фаолиятида ўтказиладиган текширувларнинг қандай турлари мавжуд? Тадбиркорлик субектлари фаолиятини “хавфни таҳлил этиш” методикаси асосида текшириш деганда нимани тушунасиз?', 13, 'Тадбиркорлик ҳуқуқи', 2, 0);
