INSERT INTO `question` VALUES (514, '4. Har bir huquqiy fan oʻzining normativ-huquqiy hujjatlar bazasiga ega boʻlib, ekologik-huquqiy bilimlar ekologiya va atrof muhitni muhofaza qilish sohasidagi qonun hujjatlariga ham asoslanadi. Ekologiya huquqi manbalari hisoblangan qonun hujjatlarini tasniflang! ', 16, '4321.0', 1, 0);
