INSERT INTO `question` VALUES (519, '9. Ekologiya va atrof muhitni muhofaza qilish sohasida Oʻzbekiston Respublikasi Ekologiya va atrof muhitni muhofaza qilish davlat qoʻmitasi faoliyati alohida ahamiyat kasb etadi. Ushbu qoʻmita faoliyatining asosiy yoʻnalishlari, vakolatlari va davlatimizda “ekologizatsiyalashtirish” jarayonini amalga oshirishdagi ahamiyatini yoriting. ', 16, '4321.0', 1, 0);
INSERT INTO `question` VALUES (520, '10. Ekologiya va atrof muhitni muhofaza qilish sohasidagi qonun talablariga qat’iy amal qilish va uni nazorat etishda Ekologiya va atrof muhitni muhofaza qilish davlat qoʻmitasi inspektorlari muhim rol oʻynaydi va ularni qonunga muvofiq bir qator vakolatlari ham mavjud. Ushbu masalani muhokama eting va Oʻzbekiston Respublikasining normativ-huquqiy hujjatlari asosida tahlil qilib huquqiy baho bering. ', 16, '4321.0', 1, 0);
INSERT INTO `question` VALUES (521, '11. Ekologik nazorat atrof tabiiy muhitni muhofaza qilish, tabiiy resurslardan oqilona foydalanish va ekologik xavfsizlikni ta’minlash qoida-talablarini barcha vazirlik, davlat qoʻmitalari va idoralari, korxonalar, tashkilotlar, muassasa, mansabdor va jismoniy shaxslar tomonidan bajarilishini tekshirishdan iborat. Oʻzbekiston Respublikasining amaldagi qonun hujjatlari asosida ekologik nazorat tushunchasi, turlari va uni amalga oshirish tartibi haqida ma’lumot bering. ', 16, '4321.0', 1, 0);
INSERT INTO `question` VALUES (522, '12. Oʻzbekiston Respublikasining “Ekologik nazorat” toʻgʻrisidagi qonuni 9-moddasiga koʻra davlat ekologik nazorati ekologik nazorat turlaridan biri hisoblanadi. Ushbu qonun normalaridan kelib chiqqan holda davlat ekologik nazorati sub’yektlari, ob’yektlari va uni amalga oshirish tartibi haqida batafsil ma’lumot bering. ', 16, '4321.0', 1, 1);
INSERT INTO `question` VALUES (523, '13. Ekologik ekspertiza deganda, rejalashtirilayotgan yoki amalga oshirilayotgan xoʻjalik va boshqa xil faoliyatning ekologik talablarga muvofiqligini belgilash hamda ekologik ekspertiza ob’yektini roʻyobga chiqarish mumkinligini aniqlash tushuniladi. Oʻzbekiston Respublikasining amaldagi qonun hujjatlari asosida ekologik ekspertiza tushunchasi, turlari, prinsiplari va uni amalga oshirish tartibi haqida ma’lumot bering. ', 16, '4321.0', 1, 0);
INSERT INTO `question` VALUES (524, '14. Ekologik ekspertiza turlari ichida davlat ekologik ekspertizasi xulosasi majburiy ahamiyat kasb etadi. Davlat ekologik ekspertizasi qaysi organ tomonidan va qanday tartibda amalga oshiriladi? ', 16, '4321.0', 1, 0);
INSERT INTO `question` VALUES (525, '15. Ekologik monitoring deganda atrof tabiiy muhitning holati va uning resurslarini kuzatish, hisobga olish, ularga baho berish va ularning istiqbolini belgilashni ta’minlash maqsadida amalga oshiriladigan chora-tadbirlar tizimi tushuniladi. Oʻzbekiston Respublikasining amaldagi qonun hujjatlari asosida ekologik monitoring tushunchasiga ta’rif bering, uning turlari va amalga oshirish maqsadini yoriting. ', 16, '4321.0', 1, 0);
INSERT INTO `question` VALUES (526, '16. Oʻzbekiston Respublikasida xoʻjalik faoliyatining atrof tabiiy muhitga zararli ta’siri atrof tabiiy muhit sifatining aholining ekologiya jihatidan xavfsizligini, tabiiy resurslarni tiklash va muhofaza qilishni kafolatlovchi normativlari va standartlari bilan cheklab qoʻyiladi. Ekologik normativlar qaysi davlat organlari tomonidan qanday maqsadlarda ishlab chiqiladi? ', 16, '4321.0', 1, 0);
INSERT INTO `question` VALUES (527, '17. “Tabiatni muhofaza qilish toʻgʻrisida”gi qonunga koʻra, ba’zi ob’yektlarning tabiiy resurslarga zararli ta’sir etgan yoki bunday ta’sir etish xavfi tugʻilgan taqdirda ularning faoliyati cheklanishi, toʻxtatib qoʻyilishi, zararli ta’sir etish sabablarini bartaraf etishning imkoni boʻlmagan taqdirda esa tugatilishi yoki oʻzgartirilishi mumkin. Tabiiy resurslardan foydalanish huquqini cheklanishi, toʻxtatib qoʻyilishi va tugatilishi (bekor boʻlishi) asoslarini yoriting. ', 16, '4321.0', 1, 0);
INSERT INTO `question` VALUES (528, '18. Qonunchilik tabiiy resurslardan maxsus foydalanganlik uchun, atrof tabiiy muhitni ifloslantirganlik (shu jumladan chiqindilarni joylashtirganlik) va atrof tabiiy muhitga boshqacha tarzda zararli ta’sir koʻrsatganlik uchun toʻlov undirishni koʻzda tutadi. Ushbu masalani Oʻzbekiston Respublikasining normativ-huquqiy hujjatlari asosida tahlil qilib huquqiy baho bering.', 16, '4321.0', 1, 0);
INSERT INTO `question` VALUES (529, '19. Ekologiya va atrof muhitni muhofaza qilish sohasidagi qonun talablariga qat’iy amal qilish muhim ahamiyat kasb etadi. Amaliyotda ekologik qonunchilik talablarini buzganlik uchun bir qator yuridik javobgarlik choralari qoʻllaniladi. Ekologiya sohasidagi qonun hujjatlarini buzganlik uchun yuridik javobgarlik choralari tushunchasi va turlari haqida batafsil ma’lumot bering. ', 16, '4321.0', 1, 0);
INSERT INTO `question` VALUES (530, '20. Yer tabiiy resurslar ichida oʻziga xos, alohida huquqiy maqomga egaligi bilan ajralib turadi. Yer bilan bogʻliq munosabatlarni tartibga solishda Oʻzbekiston Respublikasining Yer kodeksi muhim manba hisoblanadi. Oʻzbekiston Respublikasining Yer kodeksiga koʻra mamlakat butun yer fondi necha toifaga boʻlinadi? Ularning har biriga ta’rif bering. ', 16, '4321.0', 1, 0);
INSERT INTO `question` VALUES (531, '21. Yer qonunchiligi qishloq xoʻjaligiga moʻljallangan yerlarni muhofaza qilish chora-tadbirlarini belgilaydi. Yerlarni muhofaza qilish chora-tadbirlari haqida batafsil ma’lumot bering. ', 16, '4321.0', 1, 0);
INSERT INTO `question` VALUES (532, '22. Qishloq xoʻjaligigiga moʻljallangan yerlar, ayniqsa sugʻoriladigan yerlar alohida muhofaza qilinib, bunday yerlardan boshqa maqsadlarda foydalanishga qonunchilik yoʻl qoʻymaydi. Qishloq xoʻjaligiga moʻljallangan yerlardan qanday hollarda va qaysi organ tomonidan boshqa maqsadlarda foydalanishga yoʻl qoʻyiladi?', 16, '4321.0', 1, 0);
INSERT INTO `question` VALUES (533, '23. Oʻzbekiston Respublikasining “Suv va suvdan foydalanish toʻgʻrisida”gi qonuni suvdan foydalanuvchilar va suv iste’molchilarining huquqiy holatini belgilaydi. Ushbu qonunga asosan suvdan foydalanish, suv iste’moli tushunchasi va turlari haqida ma’lumot bering. ', 16, '4321.0', 1, 0);
INSERT INTO `question` VALUES (534, '24. Oʻzbekiston Respublikasining “Suv va suvdan foydalanish toʻgʻrisida”gi qonuni suv va suv ob’yektlarini muhofaza qilish qoida-talablarini belgilaydi. Suv va suv ob’yektlarini muhofaza qilishning ekologik talablarini amaldagi qonunchilik asosida yoriting. ', 16, '4321.0', 1, 0);
INSERT INTO `question` VALUES (535, '25. Yer osti boyliklari va foydali qazilmalardan foydalanishga muayyan shartlar asosida yoʻl qoʻyiladi. Yer qa’ridan oqilona foydalanish va uni muhofaza qilishning ekologik-huquqiy talablari haqida ma’lumot bering. ', 16, '4321.0', 1, 0);
INSERT INTO `question` VALUES (536, '26. Oʻzbekiston Respublikasining “Yer osti boyliklari toʻgʻrisda”gi qonunida yer qa’ridan foydalanish turlari koʻrsatilgan. Yer qa’ri uchastkalaridan foydalanish huquqini beruvchi litsenziya olish tartibini yoriting. ', 16, '4321.0', 1, 0);
INSERT INTO `question` VALUES (537, '27. Atmosfera havosining oʻziga xos tabiiy xususiyatlaridan kelib chiqqan holda uni muhofaza qilishning ekologik-huquqiy talablari belgilanadi. Atmosfera havosini muhofaza qilishning ekologik talablarini milliy va xalqaro huquqiy hujjatlar mazmunidan kelib chiqqan holda tahlil qiling. ', 16, '4321.0', 1, 0);
INSERT INTO `question` VALUES (538, '28. Oʻsimlik dunyosi biosferaning muhim tarkibiy qismlaridan biri hisoblanadi. Amaldagi qonunchilikdan kelib chiqqan holda oʻsimlik dunyosi ob’yektlaridan foydalanish turlari haqida ma’lumot bering. ', 16, '4321.0', 1, 0);
INSERT INTO `question` VALUES (539, '29. Hayvonot dunyosi ob’yektlariga kelib chiqishi hayvonlarga mansub boʻlgan organizmlar sut emizuvchilar, parrandalar, sudralib yuruvchi hayvonlar, ham quruqlikda ham suvda yashovchi hayvonlar, baliqlar, umurtqasizlar va ularning populyatsiyalari kiritiladi. Amaldagi qonunchilikdan kelib chiqqan holda hayvonot dunyosidan foydalanish turlari va usullarini yoriting.', 16, '4321.0', 1, 0);
INSERT INTO `question` VALUES (540, '30. Muhofaza etiladigan tabiiy hududlar belgilangan maqsadi va rejimiga qarab bir necha toifalarga boʻlinadi. Davlat qoʻriqxonalari va majmua (landshaft) buyurtma qoʻriqxonalarining huquqiy rejimini yoriting. ', 16, '4321.0', 1, 0);
INSERT INTO `question` VALUES (541, '31. Muhofaza etiladigan tabiiy hududlar belgilangan maqsadi va rejimiga qarab bir necha toifalarga boʻlinadi. Tabiat bogʻlari va davlat tabiat yodgorliklarining huquqiy rejimini yoriting. ', 16, '4321.0', 1, 0);
INSERT INTO `question` VALUES (542, '32. Oʻrmonlar asosan ekologik – tuproqni muhofaza qilish, suvni muhofaza qilish, oʻsimlik va hayvonot dunyosini hamda boshqa tabiiy resurslarni muhofaza qilish, himoya, sanitariya-gigiyena, sogʻlomlashtirish, rekreatsiya va boshqa ijtimoiy-iqtisodiy vazifalarni bajaradi. Oʻzbekiston Respublikasida oʻrmonlarni muhofaza qilish, himoya qilish, koʻpaytirish, takroriy koʻpaytirish, qayta tiklash, ularning mahsuldorligini oshirish va ulardan foydalanish sohasidagi davlat siyosatining asosiy yoʻnalishlarini batafsil tahlil qiling. ', 16, '4321.0', 1, 0);
INSERT INTO `question` VALUES (543, '33. Muqobil energiya va qayta tiklanuvchi energiya tushunchalari bir-biridan farqlanib, ularning har ikkisidan foydalanish uchun oʻzlashtirishni yoʻlga qoʻyish bugungi kunda dolzarb boʻlib qolmoqda. Oʻzbekiston Respublikasida muqobil energiya resurslaridan foydalanishning bugungi holati va istiqbollarini ilmiy-huquqiy jihatdan asoslang. ', 16, '4321.0', 1, 0);
INSERT INTO `question` VALUES (544, '34. Antropogen oʻzgargan hududlar deganda insonning ta’siri natijasida oʻzgargan hududlar tushuniladi. Ular sirasiga aholi punktlari, agrar tegralar, sanoat zonalari kabilar kiritiladi. Aholi punktlarida atrof muhitni muhofaza qilishning ekologik-huquqiy talablari nimalardan iborat? Amaldagi qonun hujjatlari asosida tahlil qiling. ', 16, '4321.0', 1, 0);
INSERT INTO `question` VALUES (545, '35. Antropogen oʻzgargan hududlar deganda insonning ta’siri natijasida oʻzgargan hududlar tushuniladi. Ular sirasiga aholi punktlari, agrar tegralar, sanoat zonalari kabilar kiritiladi. Sanoat zonalarida atrof muhitni muhofaza qilishning ekologik-huquqiy talablari nimalardan iborat? Amaldagi qonun hujjatlari asosida tahlil qiling. ', 16, '4321.0', 1, 0);
INSERT INTO `question` VALUES (546, '36. Oʻzbekiston Respublikasining “Chiqindilar toʻgʻrisida”gi qonunida chiqindi bilan bogʻliq ishlarni amalga oshirishning huquqiy masalalari batafsil tartibga solingan. Oʻzbekiston Respublikasida chiqindi bilan bogʻliq ishlarni amalga oshirish sohasida vakolatli boʻlgan davlat organlari faoliyatini tahlil qiling. ', 16, '4321.0', 1, 0);
INSERT INTO `question` VALUES (547, '37. Oʻzbekiston Respublikasi mustaqillik yillarida ekologiya sohasida xalqaro hamkorlik ishlarini yoʻlga qoʻydi. Oʻzbekiston Respublikasi a’zo boʻlgan xalqaro konvensiyalar haqida batafsil ma’lumot bering. ', 16, '4321.0', 1, 0);
INSERT INTO `question` VALUES (548, '38. Atrof muhitni xalqaro huquqiy muhofaza qilishda ekologik tamoyillarga rioya qilish muhim ahamiyatga ega. Atrof tabiiy muhitni xalqaro ekologik huquqiy muhofaza qilish tamoyillarining mazmun-mohiyatini yoriting.', 16, '4321.0', 1, 0);
INSERT INTO `question` VALUES (549, '39. Yer huquqi mustaqil huquq tarmogʼi sifatida oʼzining nazariy qurilmasi va bazasiga ega. Oʼzbekiston Respublikasi zamonaviy yer huquqining oʼziga xos xususiyatlari haqida maʼlumot bering.', 16, '4321.0', 1, 0);
INSERT INTO `question` VALUES (550, '40. Yer fondi davlat boshqaruvini amalga oshirishda Vazirlar Mahkamasi va mahalliy davlat hokimiyati organlari umumiy vakolatli davlat organlari sirasiga kiradi. Oʼzbekiston Respublikasi Vazirlar Mahkamasi faoliyatiga huquqiy baho bering.', 16, '4321.0', 1, 0);
INSERT INTO `question` VALUES (551, '41. Yer fondi davlat boshqaruvini amalga oshirishda mahalliy davlat hokimiyati organlari muhim vazifalarni amalga oshiradi. Oʼzbekiston Respublikasi mahalliy davlat hokimiyati organlarining yerdan foydalanish va muhofaza qilish sohasidagi faoliyatiga huquqiy baho bering.', 16, '4321.0', 1, 1);
INSERT INTO `question` VALUES (552, '42. Yer fondi davlat boshqaruvini amalga oshirishda maxsus vakolatli davlat boshqaruvi organlari faoliyati muhim ahamiyatga ega. Oʼzbekiston Respublikasi Davlat Soliq qoʼmitasi huzuridagi Kadastr agentligining sohaga oid faoliyatining huquqiy asoslari, ushbu organning vakolatlari haqida maʼlumot bering.', 16, '4321.0', 1, 1);
INSERT INTO `question` VALUES (553, '43. Yerdan foydalanish va muhofaza qilishni tashkil etishda iqtisodiy huquqiy mexanizm amal qiladi. Yerdan foydalanish va muhofaza qilish iqtisodiy mexanizmining maqsad va asosiy yoʼnalishlarini tavsiflang.', 16, '4321.0', 1, 0);
INSERT INTO `question` VALUES (554, '44. Oʼzbekiston Respublikasida yerlarni muhofaza qilish masalasiga davlat siyosati darajasida eʼtibor qaratiladi. Аmaldagi yer qonunchiligi yerlarni muhofaza qilishning qanday chora-tadbirlarini nazarda tutadi?', 16, '4321.0', 1, 0);
INSERT INTO `question` VALUES (555, '45. Oʼzbekiston Respublikasida yerlar asosiy foydalanish maqsadiga qarab bir nechta toifalarga boʼlinadi. Yer fondining har bir toifasiga qisqacha qisqacha tavsif bering.', 16, '4321.0', 1, 0);
INSERT INTO `question` VALUES (556, '46. Oʼzbekiston Respublikasi Yer kodeksining 10-moddasida yer uchastkasi tushunchasiga taʼrif berilgan. Yer uchastkasi tushunchasini tavsiflang hamda boʼlinalidan va boʼlinmaydigan yer uchastkalari tushunchalarini yoriting.', 16, '4321.0', 1, 0);
INSERT INTO `question` VALUES (557, '47. Yer fondini davlat boshqaruvining asosiy funktsiyasi boʼlib hisobga olish, rejalashtirish, yerlardan oqilona foydalanishni taʼminlash, nazorat va muhofazalash hisoblanadi. Yerdan foydalanish va muhofaza qilish sohasida davlat nazoratiga huquqiy tavsif bering. Davlat yer nazorati qaysi organlar tomonidan qanday tartibda amalga oshiriladi?', 16, '4321.0', 1, 0);
INSERT INTO `question` VALUES (558, '48. Davlat yer kadastri yer uchastkalariga boʼlgan huquqlar kafolatlanishini taʼminlash, yerlarni hisobga olish va baholash maqsadida yuritiladi. Oʼzbekiston Respublikasida davlat yer kadastrini yuritishning huquqiy asoslari va qanday tartibda yuritilishi haqida maʼlumot bering.', 16, '4321.0', 1, 0);
INSERT INTO `question` VALUES (559, '49. Yer monitoringi yer fondidagi oʼzgarishlarni oʼz vaqtida aniqlash, yerlarga baho berish, salbiy jarayonlarning oldini olish va oqibatlarini tugatish maqsadida uning holatini kuzatib borishni anglatadi. Yer monitoringini yer fondini boshqarishdagi ahamiyati nimalarda koʼrinadi? Oʼzbekiston Respublikasi yer qonunchiligiga asosan yer monitoringi qaysi organlar tomonidan qanday tartibda yuritiladi?', 16, '4321.0', 1, 0);
INSERT INTO `question` VALUES (560, '50. Fuqarolar va yuridik shaxslarning yer uchastkalariga boʼlgan huquqlari ushbu huquqlar davlat roʼyxatiga olingandan soʼng vujudga keladi. Yerga boʼlgan huquqlarni davlat roʼyxatiga olishning huquqiy tartibini yoriting.', 16, '4321.0', 1, 0);
INSERT INTO `question` VALUES (561, '1. Ўзбекистон Республикаси Жиноят кодексининг одиллик принципи мазмун-моҳиятини очиб беринг', 18, 'Жиноят ҳуқуқи', 1, 1);
INSERT INTO `question` VALUES (562, '2. Жиноятларни ижтимоий хавфлилик даражасига кўра таснифлашда қандай мезон асос қилиб олинган', 18, 'Жиноят ҳуқуқи', 1, 0);
INSERT INTO `question` VALUES (563, '3. Жиноятнинг ижтимоий хавфлилиги ва ҳуқуққа хилофлиги каби белгиларининг ўзаро нисбати қандай', 18, 'Жиноят ҳуқуқи', 1, 0);
INSERT INTO `question` VALUES (564, '4. Жиноятлар ўз хусусияти ва ижтимоий хавфлилик даражасига кўра қандай таснифланади', 18, 'Жиноят ҳуқуқи', 1, 1);
INSERT INTO `question` VALUES (565, '5. Жиноятларни сифат кўрсатгичлари ва миқдор кўрсатгичларига кўра таснифлаш нима', 18, 'Жиноят ҳуқуқи', 1, 1);
INSERT INTO `question` VALUES (566, '6. Факултатив объект деганда нима тушунилади. Унинг қўшимча объектдан фарқли жиҳатларини айтинг', 18, 'Жиноят ҳуқуқи', 1, 0);
INSERT INTO `question` VALUES (567, '7. Жиноятларнинг предмети нима ва жиноят объектидан фарқи нима. Ўз.Р ЖК дан амалий мисоллар билан фарқли жиҳатларини тушунтириб беринг', 18, 'Жиноят ҳуқуқи', 1, 1);
INSERT INTO `question` VALUES (568, '8. Жиноят объектив томонининг зарурий ва факультатив белгилари ҳамда уларнинг аҳамиятини айтинг', 18, 'Жиноят ҳуқуқи', 1, 1);
INSERT INTO `question` VALUES (569, '9. Ҳаракатсизликнинг жиноят-ҳуқуқий аҳамиятини аниқлаш учун қандай белгилари аниқланиши керак', 18, 'Жиноят ҳуқуқи', 1, 0);
INSERT INTO `question` VALUES (570, '10. Жиноий оқибатларнинг турларини айтинг. Жиноий оқибатлар турларини амалий мисоллар билан асослаб беринг', 18, 'Жиноят ҳуқуқи', 1, 0);
INSERT INTO `question` VALUES (571, '11. Жиноий моҳиятга эга бўлган сабабий боғланиш қандай мезонларга (белгиларга) жавоб бериши керак', 18, 'Жиноят ҳуқуқи', 1, 1);
INSERT INTO `question` VALUES (572, '12. Жиноят объектив томони факультатив белгиларини жиноятни квалификация қилишдаги аҳамияти нималарда ифодаланади', 18, 'Жиноят ҳуқуқи', 1, 1);
INSERT INTO `question` VALUES (573, '13. Жиноят таркиби субъектив томонининг факултатив белгиларининг аҳамияти ва уларнинг роли қандай', 18, 'Жиноят ҳуқуқи', 1, 0);
INSERT INTO `question` VALUES (574, '14. Мураккаб айбли жиноятларни тушунтириб беринг. Бундай турдаги жиноятлар тўғрисида қандай ҳолатларда гапириш мумкин', 18, 'Жиноят ҳуқуқи', 1, 1);
INSERT INTO `question` VALUES (575, '15. Айбсиз ҳолда зарар етказиш мазмунини ёритиб беринг', 18, 'Жиноят ҳуқуқи', 1, 1);
INSERT INTO `question` VALUES (576, '16. Жиноят мотиви ва мақсадининг жиноят-ҳуқуқий аҳамияти нимадан иборат. Уларнинг фарқи нимада', 18, 'Жиноят ҳуқуқи', 1, 1);
INSERT INTO `question` VALUES (577, '17. Юридик ва фактик хатоларнинг турларини айтиб беринг', 18, 'Жиноят ҳуқуқи', 1, 1);
INSERT INTO `question` VALUES (578, '18. Жиноят содир этган вояга етмаган шахснинг ёшини тасдиқловчи ҳужжатлар йўқлиги туфайли унинг ёши номаълум бўлган ҳолларда мазкур шахсни жиноий жавобгарликка тортиш масаласи қандай ҳал қилинади', 18, 'Жиноят ҳуқуқи', 1, 1);
INSERT INTO `question` VALUES (579, '19. Ақли норасоликнинг юридик (психологик) ва тиббий мезонлари нималардан иборат', 18, 'Жиноят ҳуқуқи', 1, 1);
INSERT INTO `question` VALUES (580, '20. Махсус субъектнинг белгиларини айтиб беринг. Уни умумий субъектдан нима фарқли жиҳатлари бор', 18, 'Жиноят ҳуқуқи', 1, 1);
