INSERT INTO `question` VALUES (1485, '10.  Мақсадларни тўғри қўйиш ва уларга эришиш бахтли ҳаёт гаровидир. Бироқ ҳар доим ҳам интилаётган нарсаларимизни тўғри ифодалай олмаслик ҳам кузатилади. Савол: Нима учун баъзан қўйилган мақсадга етишиш қийин туюлади?', 33, NULL, 1, 0);
