INSERT INTO `question` VALUES (1514, '39.   Ҳуқуқшуноснинг масалага ижодий ёндашуви фақат қонунлар доирасида, уларга амал қилган ҳолда қўлланилиши лозим, деган талабни изоҳлаб беринг.', 33, NULL, 1, 0);
