INSERT INTO `questions_kazus` VALUES (6, '6. Хизмат сафарига ходим 1 кун олдин жўнаб кетгандан кейин буйруқ чиқарилди. Ходимга ҳисоб-китоб қандай амалга оширилади, батафсил тушунтириб беринг.', 1, 0, 0);
