INSERT INTO `questions_kazus` VALUES (16, '16. Олий таълим муассасалари талабаси ўқув семестри якуни бўйича фанлардан фақат \"аъло\" ўзлаштириш кўрсаткичига эга бўлганлигини важ қилиб “5” лик стипендия тўлашни талаб қилди. Бухгалтерия ходимлари ушбу тоифадаги стипендия тўлаш мумкинмаслигини айтиб ўтди. Талабанинг сўрови тўғрими? Бухгалтерия ходими қандай асосларда рад этмоқда, батафсил тушунтириб беринг.', 1, 0, 0);
