INSERT INTO `questions_kazus` VALUES (182, '4. Қуйидаги тикловчи одил судловнинг белгисини тушунтириб беринг? “A flexible response to the circumstances of the crime, the offender and the victim, one that allows each case to be considered individually”', 7, 0, 0);
INSERT INTO `questions_kazus` VALUES (183, '5. Қуйидаги тикловчи одил судловнинг белгисини тушунтириб беринг? “A response to crime that respects the dignity and equality of each person, builds understanding and promotes social harmony through the healing of victims, offenders and communities”', 7, 0, 1);
INSERT INTO `questions_kazus` VALUES (184, '6. Қуйидаги тикловчи одил судловнинг белгисини тушунтириб беринг? “ A viable alternative in many cases to the formal criminal justice system and its stigmatizing effects on offenders”', 7, 0, 0);
INSERT INTO `questions_kazus` VALUES (185, '7. Қуйидаги тикловчи одил судловнинг белгисини тушунтириб беринг? “An approach that can be used in conjunction with traditional criminal justice processes and sanctions”', 7, 0, 1);
INSERT INTO `questions_kazus` VALUES (186, '8. Қуйидаги тикловчи одил судловнинг белгисини тушунтириб беринг? “An approach that incorporates problem solving and addressing the underlying causes of conflict”', 7, 0, 0);
INSERT INTO `questions_kazus` VALUES (187, '9. Қуйидаги тикловчи одил судловнинг белгисини тушунтириб беринг? “An approach that addresses the harms and needs of victims”', 7, 0, 0);
INSERT INTO `questions_kazus` VALUES (188, '10. Қуйидаги тикловчи одил судловнинг белгисини тушунтириб беринг? An approach which encourages an offender to gain insight into the causes and effects of his or her behaviour and take responsibility in a meaningful way”', 7, 0, 0);
INSERT INTO `questions_kazus` VALUES (189, '11. Қуйидаги тикловчи одил судловнинг белгисини тушунтириб беринг? “A flexible and variable approach which can be adapted to the circumstances, legal tradition, principles and underlying philosophies of established national criminal justice systems”', 7, 0, 0);
INSERT INTO `questions_kazus` VALUES (190, '12. Қуйидаги тикловчи одил судловнинг белгисини тушунтириб беринг? “An approach that is suitable for dealing with many different kinds of offences and offenders, including many very serious offences”', 7, 0, 0);
INSERT INTO `questions_kazus` VALUES (191, '13. Қуйидаги тикловчи одил судловнинг белгисини тушунтириб беринг? “A response to crime which is particularly suitable for situations where juvenile offenders are involved and in which an important objective of the intervention is to teach the offenders some new values and skills”', 7, 0, 0);
INSERT INTO `questions_kazus` VALUES (192, '14. Қуйидаги тикловчи одил судловнинг белгисини тушунтириб беринг? “A response that recognizes the role of the community as a prime site of preventing and responding to crime and social disorder”', 7, 0, 0);
INSERT INTO `questions_kazus` VALUES (193, '15. Қуйидаги тикловчи одил судловнинг принципини мазмун-моҳиятини тушунтириб беринг? “The right to consult with legal counsel”', 7, 0, 0);
INSERT INTO `questions_kazus` VALUES (194, '16. Қуйидаги тикловчи одил судловнинг принципини мазмун-моҳиятини тушунтириб беринг? “The right of minors to the assistance of a parent or guardian”', 7, 0, 1);
INSERT INTO `questions_kazus` VALUES (195, '17. Қуйидаги тикловчи одил судловнинг принципини мазмун-моҳиятини тушунтириб беринг? “The right to be fully informed”', 7, 0, 0);
