INSERT INTO `questions_logic` VALUES (801, 'Иш вақтининг қисқартирилган муддати ва уни қўллаш тартибини тушунтириб беринг.', 15, 0, 0);
INSERT INTO `questions_logic` VALUES (802, 'Тўлиқсиз иш вақти ва уни қўллаш тартибини тушунтириб беринг.', 15, 0, 0);
INSERT INTO `questions_logic` VALUES (803, 'Ижтимоий таътилларни тушунтириб беринг.', 15, 0, 1);
INSERT INTO `questions_logic` VALUES (804, 'Иш ҳақи сақланмаган ҳолда бериладиган таътилларни тушунтириб беринг.', 15, 0, 1);
INSERT INTO `questions_logic` VALUES (805, 'Фойдаланилмаган таътиллар учун пуллик компенсация тўлаш тартибини тушунтириб беринг.', 15, 0, 0);
INSERT INTO `questions_logic` VALUES (806, 'Меҳнат ҳақининг энг кам миқдорини тушунтириб беринг.', 15, 0, 0);
INSERT INTO `questions_logic` VALUES (807, 'Меҳнат ҳақидан ушлаб қолиш асослари ва тартибини тушунтириб беринг.', 15, 0, 0);
INSERT INTO `questions_logic` VALUES (808, 'Хизмат сафари даврида харажатларни қоплаш тартибини тушунтириб беринг.', 15, 0, 0);
INSERT INTO `questions_logic` VALUES (809, 'Тиббий кўрик ва унинг аҳамиятини тушунтириб беринг.', 15, 0, 0);
INSERT INTO `questions_logic` VALUES (810, 'Ишлаб чиқаришдаги бахтсиз ҳодисаларни ҳисобга олиб бориш ва текшириш тартибини ёритиб беринг. ', 15, 0, 0);
INSERT INTO `questions_logic` VALUES (811, 'Ҳомиладор аёлларни ва боласи бор аёлларни меҳнат соҳасидаги кафолатларини ёритиб беринг.', 15, 0, 0);
INSERT INTO `questions_logic` VALUES (812, 'Ўн саккиз ёшга тўлмаган шахсларни меҳнат соҳасидаги кафолатларини ёритиб беринг.', 15, 0, 0);
INSERT INTO `questions_logic` VALUES (813, 'Меҳнат низосини ҳал қилишни сўраб мурожаат этиш муддатларини ёритиб беринг.', 15, 0, 0);
INSERT INTO `questions_logic` VALUES (814, 'Меҳнатга доир қайси тоифадаги ишлар бўйича қарорлар дарҳол ижро этилиши лозим. ', 15, 0, 0);
INSERT INTO `questions_logic` VALUES (815, 'Oʻzbekiston Respublikasi Prezidentining 2019-yil 30-oktabr kuni “2030-yilgacha boʻlgan davrda Oʻzbekiston Respublikasining atrof muhitni muhofaza qilish Kontsepsiyasini tasdiqlash toʻgʻrisida”gi PF–5863-sonli Farmoni qabul qilingan. \r\nUshbu Farmonning asosiy mazmuni va atrof muhitni muhofaza qilish sohasidagi ahamiyatini yoriting. \r\n', 16, 0, 0);
INSERT INTO `questions_logic` VALUES (816, 'Oʻzbekiston Respublikasining “Tabiatni muhofaza qilish toʻgʻrisida”gi qonuniga koʻra tabiiy resurslardan umumiy tarzda va maxsus yoʻsinda foydalanishga yoʻl qoʻyiladi. \r\nTabiatdan maxsus foydalanish huquqi tushunchasiga ekologik qonunchilik nuqtai nazaridan tavsif bering va amaliy misollar keltiring. \r\n', 16, 0, 0);
INSERT INTO `questions_logic` VALUES (817, 'Oʻzbekiston Respublikasi Prezidenti Shavkat Mirziyoyev BMTning 45-sessiyasida butun jahon hamjamiyatining e’tiborini Orol muammosi masalalariga qaratdi. \r\nOrolboʻyi hududidagi ekologik vaziyatni barqarorlashtirish va salbiy oqibatlarni yumshatish boʻyicha bugungi kunda davlatimizda qanday chora-tadbirlar amalga oshirilmoqda? Oʻz fikringizni siyosiy-huquqiy nuqtai nazardan yoriting. \r\n', 16, 0, 1);
INSERT INTO `questions_logic` VALUES (818, 'Ekologiya huquqi fani umumiy va maxsus qismlardan iborat boʻlib, ularning har biri muayyan institutlarni birlashtiradi. \r\nUshbu institutlar qanday munosabatlarni oʻrganadi va ularning uzviy bogʻliqligi nimada? \r\n', 16, 0, 0);
INSERT INTO `questions_logic` VALUES (819, 'Har bir huquqiy fan oʻzining normativ-huquqiy hujjatlar bazasiga ega boʻlib, ekologik-huquqiy bilimlar ekologiya va atrof muhitni muhofaza qilish sohasidagi qonun hujjatlariga ham asoslanadi. \r\nEkologiya huquqi manbalari hisoblangan qonun hujjatlarini tasniflang! \r\n', 16, 0, 0);
INSERT INTO `questions_logic` VALUES (820, 'Ekologik huquqiy munosabatlar oʻziga xos tamoyillarga asoslanadi. \r\nEkologiya huquqining tamoyillari, ularning mazmun-mohiyatini yoriting. \r\n', 16, 0, 0);
INSERT INTO `questions_logic` VALUES (821, 'Bozor munosabatlari sharoitida tabiiy resurslarga nisbatan davlat mulkchiligi bilan bir qatorda xususiy mulkchilik ham belgilanadi. \r\nOʻzbekiston Respublikasida tabiiy resurslarga nisbatan mulkchilik haqida amaldagi qonun hujjatlari asosida ma’lumot bering. \r\n', 16, 0, 0);
INSERT INTO `questions_logic` VALUES (822, 'Ekologiya huquqi fanida fuqarolarning yashash uchun qulay atrof tabiiy muhitga ega boʻlish huquqi ta’minlanishi masalalari oʻrganilidi. \r\nOʻzbekiston Respublikasining ekologiya sohasidagi qonun hujjatlari normalariga asoslangan holda “Fuqarolarning yashash uchun qulay atrof tabiiy muhitga ega boʻlish huquqi”ga tavsif bering. \r\n', 16, 0, 0);
INSERT INTO `questions_logic` VALUES (823, 'Oʻzbekiston Respublikasining davlat ekologik siyosati davlat boshqaruvining umumiy va maxsus vakolatli organlari tomonidan amalga oshiriladi. \r\nEkologiya sohasidagi umumiy vakolatli davlat boshqaruvi organlari faoliyatini yoriting. \r\n', 16, 0, 0);
INSERT INTO `questions_logic` VALUES (824, 'Ekologiya va atrof muhitni muhofaza qilish sohasida Oʻzbekiston Respublikasi Ekologiya va atrof muhitni muhofaza qilish davlat qoʻmitasi faoliyati alohida ahamiyat kasb etadi. \r\nUshbu qoʻmita faoliyatining asosiy yoʻnalishlari, vakolatlari va davlatimizda “ekologizatsiyalashtirish” jarayonini amalga oshirishdagi ahamiyatini yoriting. \r\n', 16, 0, 0);
INSERT INTO `questions_logic` VALUES (825, 'Ekologiya va atrof muhitni muhofaza qilish sohasidagi qonun talablariga qat’iy amal qilish va uni nazorat etishda Ekologiya va atrof muhitni muhofaza qilish davlat qoʻmitasi inspektorlari muhim rol oʻynaydi va ularni qonunga muvofiq bir qator vakolatlari ham mavjud. \r\nUshbu masalani muhokama eting va Oʻzbekiston Respublikasining normativ-huquqiy hujjatlari asosida tahlil qilib huquqiy baho bering. \r\n', 16, 0, 1);
INSERT INTO `questions_logic` VALUES (826, 'Ekologik nazorat atrof tabiiy muhitni muhofaza qilish, tabiiy resurslardan oqilona foydalanish va ekologik xavfsizlikni ta’minlash qoida-talablarini barcha vazirlik, davlat qoʻmitalari va idoralari, korxonalar, tashkilotlar, muassasa, mansabdor va jismoniy shaxslar tomonidan bajarilishini tekshirishdan iborat. \r\nOʻzbekiston Respublikasining amaldagi qonun hujjatlari asosida ekologik nazorat tushunchasi, turlari va uni amalga oshirish tartibi haqida ma’lumot bering. \r\n', 16, 0, 0);
INSERT INTO `questions_logic` VALUES (827, 'Oʻzbekiston Respublikasining “Ekologik nazorat” toʻgʻrisidagi qonuni 9-moddasiga koʻra davlat ekologik nazorati ekologik nazorat turlaridan biri hisoblanadi. \r\nUshbu qonun normalaridan kelib chiqqan holda davlat ekologik nazorati sub’yektlari, ob’yektlari va uni amalga oshirish tartibi haqida batafsil ma’lumot bering. \r\n', 16, 0, 0);
INSERT INTO `questions_logic` VALUES (828, 'Ekologik ekspertiza deganda, rejalashtirilayotgan yoki amalga oshirilayotgan xoʻjalik va boshqa xil faoliyatning ekologik talablarga muvofiqligini belgilash hamda ekologik ekspertiza ob’yektini roʻyobga chiqarish mumkinligini aniqlash tushuniladi. \r\nOʻzbekiston Respublikasining amaldagi qonun hujjatlari asosida ekologik ekspertiza tushunchasi, turlari, prinsiplari va uni amalga oshirish tartibi haqida ma’lumot bering. \r\n', 16, 0, 0);
INSERT INTO `questions_logic` VALUES (829, 'Ekologik ekspertiza turlari ichida davlat ekologik ekspertizasi xulosasi majburiy ahamiyat kasb etadi. \r\nDavlat ekologik ekspertizasi qaysi organ tomonidan va qanday tartibda amalga oshiriladi? \r\n', 16, 0, 1);
INSERT INTO `questions_logic` VALUES (830, 'Ekologik monitoring deganda atrof tabiiy muhitning holati va uning resurslarini kuzatish, hisobga olish, ularga baho berish va ularning istiqbolini belgilashni ta’minlash maqsadida amalga oshiriladigan chora-tadbirlar tizimi tushuniladi. \r\nOʻzbekiston Respublikasining amaldagi qonun hujjatlari asosida ekologik monitoring tushunchasiga ta’rif bering, uning turlari va amalga oshirish maqsadini yoriting. \r\n', 16, 0, 0);
INSERT INTO `questions_logic` VALUES (831, 'Oʻzbekiston Respublikasida xoʻjalik faoliyatining atrof tabiiy muhitga zararli ta’siri atrof tabiiy muhit sifatining aholining ekologiya jihatidan xavfsizligini, tabiiy resurslarni tiklash va muhofaza qilishni kafolatlovchi normativlari va standartlari bilan cheklab qoʻyiladi. \r\nEkologik normativlar qaysi davlat organlari tomonidan qanday maqsadlarda ishlab chiqiladi? \r\n', 16, 0, 0);
INSERT INTO `questions_logic` VALUES (832, '“Tabiatni muhofaza qilish toʻgʻrisida”gi qonunga koʻra, ba’zi ob’yektlarning tabiiy resurslarga zararli ta’sir etgan yoki bunday ta’sir etish xavfi tugʻilgan taqdirda ularning faoliyati cheklanishi, toʻxtatib qoʻyilishi, zararli ta’sir etish sabablarini bartaraf etishning imkoni boʻlmagan taqdirda esa tugatilishi yoki oʻzgartirilishi mumkin. \r\nTabiiy resurslardan foydalanish huquqini cheklanishi, toʻxtatib qoʻyilishi va tugatilishi (bekor boʻlishi) asoslarini yoriting. \r\n', 16, 0, 0);
INSERT INTO `questions_logic` VALUES (833, 'Qonunchilik tabiiy resurslardan maxsus foydalanganlik uchun, atrof tabiiy muhitni ifloslantirganlik (shu jumladan chiqindilarni joylashtirganlik) va atrof tabiiy muhitga boshqacha tarzda zararli ta’sir koʻrsatganlik uchun toʻlov undirishni koʻzda tutadi. \r\nUshbu masalani Oʻzbekiston Respublikasining normativ-huquqiy hujjatlari asosida tahlil qilib huquqiy baho bering.\r\n', 16, 0, 1);
INSERT INTO `questions_logic` VALUES (834, 'Ekologiya va atrof muhitni muhofaza qilish sohasidagi qonun talablariga qat’iy amal qilish muhim ahamiyat kasb etadi. Amaliyotda ekologik qonunchilik talablarini buzganlik uchun bir qator yuridik javobgarlik choralari qoʻllaniladi. \r\nEkologiya sohasidagi qonun hujjatlarini buzganlik uchun yuridik javobgarlik choralari tushunchasi va turlari haqida batafsil ma’lumot bering. \r\n', 16, 0, 1);
INSERT INTO `questions_logic` VALUES (835, 'Yer tabiiy resurslar ichida oʻziga xos, alohida huquqiy maqomga egaligi bilan ajralib turadi. Yer bilan bogʻliq munosabatlarni tartibga solishda Oʻzbekiston Respublikasining Yer kodeksi muhim manba hisoblanadi. \r\nOʻzbekiston Respublikasining Yer kodeksiga koʻra mamlakat butun yer fondi necha toifaga boʻlinadi? Ularning har biriga ta’rif bering. \r\n', 16, 0, 0);
INSERT INTO `questions_logic` VALUES (836, 'Yer qonunchiligi qishloq xoʻjaligiga moʻljallangan yerlarni muhofaza qilish chora-tadbirlarini belgilaydi. \r\nYerlarni muhofaza qilish chora-tadbirlari haqida batafsil ma’lumot bering. \r\n', 16, 0, 0);
INSERT INTO `questions_logic` VALUES (837, 'Qishloq xoʻjaligigiga moʻljallangan yerlar, ayniqsa sugʻoriladigan yerlar alohida muhofaza qilinib, bunday yerlardan boshqa maqsadlarda foydalanishga qonunchilik yoʻl qoʻymaydi. \r\nQishloq xoʻjaligiga moʻljallangan yerlardan qanday hollarda va qaysi organ tomonidan boshqa maqsadlarda foydalanishga yoʻl qoʻyiladi? \r\n', 16, 0, 0);
INSERT INTO `questions_logic` VALUES (838, 'Oʻzbekiston Respublikasining “Suv va suvdan foydalanish toʻgʻrisida”gi qonuni suvdan foydalanuvchilar va suv iste’molchilarining huquqiy holatini belgilaydi. \r\nUshbu qonunga asosan suvdan foydalanish, suv iste’moli tushunchasi va turlari haqida ma’lumot bering. \r\n', 16, 0, 0);
INSERT INTO `questions_logic` VALUES (839, 'Oʻzbekiston Respublikasining “Yer osti boyliklari toʻgʻrisda”gi qonunida yer qa’ridan foydalanish turlari koʻrsatilgan. \r\nYer qa’ri uchastkalaridan foydalanish huquqini beruvchi litsenziya olish tartibini yoriting. \r\n', 16, 0, 0);
INSERT INTO `questions_logic` VALUES (840, 'Atmosfera havosining oʻziga xos tabiiy xususiyatlaridan kelib chiqqan holda uni muhofaza qilishning ekologik-huquqiy talablari belgilanadi. \r\nAtmosfera havosini muhofaza qilishning ekologik talablarini milliy va xalqaro huquqiy hujjatlar mazmunidan kelib chiqqan holda tahlil qiling. \r\n', 16, 0, 1);
INSERT INTO `questions_logic` VALUES (841, 'Oʻsimlik dunyosi biosferaning muhim tarkibiy qismlaridan biri hisoblanadi. \r\nAmaldagi qonunchilikdan kelib chiqqan holda oʻsimlik dunyosi ob’yektlaridan foydalanish turlari haqida ma’lumot bering. \r\n', 16, 0, 0);
INSERT INTO `questions_logic` VALUES (842, 'Hayvonot dunyosi ob’yektlariga kelib chiqishi hayvonlarga mansub boʻlgan organizmlar sut emizuvchilar, parrandalar, sudralib yuruvchi hayvonlar, ham quruqlikda ham suvda yashovchi hayvonlar, baliqlar, umurtqasizlar va ularning populyatsiyalari kiritiladi. \r\nAmaldagi qonunchilikdan kelib chiqqan holda hayvonot dunyosidan foydalanish turlari va usullarini yoriting.\r\n', 16, 0, 0);
INSERT INTO `questions_logic` VALUES (843, 'Muhofaza etiladigan tabiiy hududlar belgilangan maqsadi va rejimiga qarab bir necha toifalarga boʻlinadi. \r\nDavlat qoʻriqxonalari va majmua (landshaft) buyurtma qoʻriqxonalarining huquqiy rejimini yoriting. \r\n', 16, 0, 0);
INSERT INTO `questions_logic` VALUES (844, 'Oʻrmonlar asosan ekologik – tuproqni muhofaza qilish, suvni muhofaza qilish, oʻsimlik va hayvonot dunyosini hamda boshqa tabiiy resurslarni muhofaza qilish, himoya, sanitariya-gigiyena, sogʻlomlashtirish, rekreatsiya va boshqa ijtimoiy-iqtisodiy vazifalarni bajaradi. \r\nOʻzbekiston Respublikasida oʻrmonlarni muhofaza qilish, himoya qilish, koʻpaytirish, takroriy koʻpaytirish, qayta tiklash, ularning mahsuldorligini oshirish va ulardan foydalanish sohasidagi davlat siyosatining asosiy yoʻnalishlarini batafsil tahlil qiling. \r\n', 16, 0, 0);
INSERT INTO `questions_logic` VALUES (845, 'Muqobil energiya va qayta tiklanuvchi energiya tushunchalari bir-biridan farqlanib, ularning har ikkisidan foydalanish uchun oʻzlashtirishni yoʻlga qoʻyish bugungi kunda dolzarb boʻlib qolmoqda. \r\nOʻzbekiston Respublikasida muqobil energiya resurslaridan foydalanishning bugungi holati va istiqbollarini ilmiy-huquqiy jihatdan asoslang. \r\n', 16, 0, 0);
INSERT INTO `questions_logic` VALUES (846, 'Antropogen oʻzgargan hududlar deganda insonning ta’siri natijasida oʻzgargan hududlar tushuniladi. Ular sirasiga aholi punktlari, agrar tegralar, sanoat zonalari kabilar kiritiladi. \r\nAholi punktlarida atrof muhitni muhofaza qilishning ekologik-huquqiy talablari nimalardan iborat? Amaldagi qonun hujjatlari asosida tahlil qiling. \r\n', 16, 0, 1);
INSERT INTO `questions_logic` VALUES (847, 'Sanoat zonalarida atrof muhitni muhofaza qilishning ekologik-huquqiy talablari nimalardan iborat? Amaldagi qonun hujjatlari asosida tahlil qiling. ', 16, 0, 0);
INSERT INTO `questions_logic` VALUES (848, 'Oʻzbekiston Respublikasining “Chiqindilar toʻgʻrisida”gi qonunida chiqindi bilan bogʻliq ishlarni amalga oshirishning huquqiy masalalari batafsil tartibga solingan. \r\nOʻzbekiston Respublikasida chiqindi bilan bogʻliq ishlarni amalga oshirish sohasida vakolatli boʻlgan davlat organlari faoliyatini tahlil qiling. \r\n', 16, 0, 0);
INSERT INTO `questions_logic` VALUES (849, 'Oʻzbekiston Respublikasi mustaqillik yillarida ekologiya sohasida xalqaro hamkorlik ishlarini yoʻlga qoʻydi. \r\nOʻzbekiston Respublikasi a’zo boʻlgan xalqaro konvensiyalar haqida batafsil ma’lumot bering. \r\n', 16, 0, 0);
INSERT INTO `questions_logic` VALUES (850, 'Yer huquqi mustaqil huquq tarmogʼi sifatida oʼzining nazariy qurilmasi va bazasiga ega. Oʼzbekiston Respublikasi zamonaviy yer huquqining oʼziga xos xususiyatlari haqida maʼlumot bering.', 16, 0, 0);
INSERT INTO `questions_logic` VALUES (851, 'Yer fondi davlat boshqaruvini amalga oshirishda Vazirlar Mahkamasi va mahalliy davlat hokimiyati organlari umumiy vakolatli davlat organlari sirasiga kiradi.\r\n Oʼzbekiston Respublikasi Vazirlar Mahkamasi faoliyatiga huquqiy baho bering.\r\n', 16, 0, 0);
INSERT INTO `questions_logic` VALUES (852, 'Yer fondi davlat boshqaruvini amalga oshirishda mahalliy davlat hokimiyati organlari muhim vazifalarni amalga oshiradi. \r\nOʼzbekiston Respublikasi mahalliy davlat hokimiyati organlarining yerdan foydalanish va muhofaza qilish sohasidagi faoliyatiga huquqiy baho bering.\r\n', 16, 0, 0);
INSERT INTO `questions_logic` VALUES (853, 'Yer fondi davlat boshqaruvini amalga oshirishda maxsus vakolatli davlat boshqaruvi organlari faoliyati muhim ahamiyatga ega. \r\nOʼzbekiston Respublikasi Davlat Soliq qoʼmitasi huzuridagi Kadastr agentligining sohaga oid faoliyatining huquqiy asoslari, ushbu organning vakolatlari haqida maʼlumot bering.\r\n', 16, 0, 1);
INSERT INTO `questions_logic` VALUES (854, 'Қилмишнинг жиноийлигини истисно қилувчи ҳолатлар, ушбу ҳолатларни қилмишнинг жиноийлигини истисно қилувчи ҳолат сифатида баҳолаш мезонларини муҳокама этинг.', 17, 0, 0);
INSERT INTO `questions_logic` VALUES (855, 'Зарурий мудофаа чегарасидан четга чиқиш деб ҳисоблашнинг шартларини айтинг. Хаёлий мудофаа нима.', 17, 0, 0);
INSERT INTO `questions_logic` VALUES (856, 'Зарурий мудофаа ва унинг охирги заруратдан фарқларини муҳокама этинг.', 17, 0, 0);
INSERT INTO `questions_logic` VALUES (857, 'Ижтимоий хавфли қилмиш содир этган шахсни ушлаш вақтида унга етказилган зарарни жиноят деб ҳисобламасликнинг шартларини айтинг.', 17, 0, 0);
INSERT INTO `questions_logic` VALUES (858, 'Буйруқ ёки бошқа вазифани ижро этишни жиноят деб ҳисобламасликнинг шартларини айтинг.', 17, 0, 0);
INSERT INTO `questions_logic` VALUES (859, 'Жиноий жазо тушунчаси, мақсади ва тизимини муҳокама этинг.', 17, 0, 0);
INSERT INTO `questions_logic` VALUES (860, 'Жиноий жазони бошқа мажбурлов чораларидан фарқи нимада ифодаланади.', 17, 0, 0);
INSERT INTO `questions_logic` VALUES (861, 'Жиноят-ҳуқуқий доктринада жазолар турларининг қандай таснифлари мавжуд.', 17, 0, 0);
INSERT INTO `questions_logic` VALUES (862, 'Жазонинг қандай турлари асосий жазоларга, қайсилари қўшимча жазоларга киради? Aралаш турдаги жазолар мавжудми?', 17, 0, 0);
