INSERT INTO `questions_logic` VALUES (990, 'Судда жиноят ишларининг очиқ ва ёпиқ тарзда кўрилишини таҳлил қилинг. Жиноят ишларни судларда ёпиқ тарзда кўришга яна қандай тоифадаги ишларни қўшиш таклифини бера оласиз ва бунинг натижасида нимага эришиш мумкин.', 18, 0, 0);
